import React, { useState, useEffect } from 'react';
import reportsApi from '../api/reports';

const FinancialImpact = () => {
  const [riskLevel, setRiskLevel] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const calculateRiskLevel = async () => {
      try {
        const data = await reportsApi.getReports();
        const nearbyReports = data.content.filter(report => 
          report.status === 'VERIFIED' && 
          (report.severity === 'HIGH' || report.severity === 'EXTREME')
        );

        // Simple risk calculation based on number and severity of nearby reports
        const riskScore = nearbyReports.reduce((score, report) => {
          return score + (report.severity === 'EXTREME' ? 2 : 1);
        }, 0);

        if (riskScore >= 5) {
          setRiskLevel({ level: 'HIGH', color: 'red', message: 'High risk of financial impact' });
        } else if (riskScore >= 3) {
          setRiskLevel({ level: 'MEDIUM', color: 'orange', message: 'Moderate risk of financial impact' });
        } else if (riskScore >= 1) {
          setRiskLevel({ level: 'LOW', color: 'yellow', message: 'Low risk of financial impact' });
        } else {
          setRiskLevel({ level: 'NONE', color: 'green', message: 'No significant financial risk' });
        }
      } catch (err) {
        setError('Failed to calculate risk level');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    calculateRiskLevel();
    // Refresh risk assessment every 15 minutes
    const interval = setInterval(calculateRiskLevel, 15 * 60 * 1000);
    return () => clearInterval(interval);
  }, []);

  if (loading) return <div className="bg-white rounded shadow p-4">Calculating risk level...</div>;
  if (error) return <div className="bg-white rounded shadow p-4 text-red-600">{error}</div>;

  return (
    <div className="bg-white rounded shadow p-4">
      <h3 className="font-bold text-lg mb-4 text-primary-700">Financial Risk Assessment</h3>
      <div className={`p-4 rounded-lg bg-${riskLevel.color}-50 border border-${riskLevel.color}-200`}>
        <div className="flex items-center justify-between">
          <div>
            <h4 className="font-semibold text-lg">Current Risk Level: {riskLevel.level}</h4>
            <p className="text-gray-600 mt-1">{riskLevel.message}</p>
          </div>
          <div className={`w-16 h-16 rounded-full bg-${riskLevel.color}-100 flex items-center justify-center`}>
            <span className={`text-${riskLevel.color}-800 text-2xl font-bold`}>
              {riskLevel.level === 'HIGH' ? '!' : riskLevel.level === 'MEDIUM' ? '!' : '✓'}
            </span>
          </div>
        </div>
        <div className="mt-4 text-sm text-gray-500">
          <p>Based on verified flood reports in your area</p>
          <p>Last updated: {new Date().toLocaleString()}</p>
        </div>
      </div>
    </div>
  );
};

export default FinancialImpact;
