import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8080/api';

const reportsApi = {
  async submitReport(reportData) {
    const formData = new FormData();
    Object.keys(reportData).forEach(key => {
      if (key === 'image' && reportData[key]) {
        formData.append('image', reportData[key]);
      } else {
        formData.append(key, reportData[key]);
      }
    });

    const response = await axios.post(`${API_URL}/reports`, formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    });
    return response.data;
  },

  async getReports(page = 0, size = 10) {
    const response = await axios.get(`${API_URL}/reports`, {
      params: { page, size }
    });
    return response.data;
  },

  async getMyReports(page = 0, size = 10) {
    const response = await axios.get(`${API_URL}/reports/my`, {
      params: { page, size }
    });
    return response.data;
  },

  async getNearbyReports(latitude, longitude, radius = 5000) {
    const response = await axios.get(`${API_URL}/reports/nearby`, {
      params: { latitude, longitude, radius }
    });
    return response.data;
  }
};

export default reportsApi;
